import styled from "styled-components";
import { useContext, useEffect } from "react";
import { AppContext } from "./AppContext";
// import tombstone from "../assets/tombstone.png";

const Confirmation = () => {
  const { reservationId, currentReservation, setCurrentReservation } =
    useContext(AppContext);
  useEffect(() => {
    (async () => {
      try {
        const data = await fetch(
          `/api/get-reservation?reservationId=${reservationId}`
        );
        const { reservation } = await data.json();
        setCurrentReservation(reservation);
      } catch (err) {
        console.log(err);
      }
    })();
  }, [setCurrentReservation, reservationId]);

  if (!currentReservation) return null;
  const { _id, flight, seat, givenName, surname, email } = currentReservation;

  return (
    <Wrapper>
      <BookingContainer>
        <h3>Your booking is confirmed!</h3>

        <Details style={{ display: "flex", flexDirection: "column" }}>
          <Item>Booking ID: {_id}</Item>
          <Item>
            Passenger: {givenName} {surname}
          </Item>
          <Item>Contact: {email}</Item>
          <Item>Flight Number: {flight}</Item>
          <Item>Seat: {seat}</Item>
        </Details>
      </BookingContainer>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  display: flex;
  height: 100%;
  justify-content: center;
  align-items: center;
  font-family: Kosugi;
`;

const BookingContainer = styled.div`
  display: flex;
  gap: 25px;
  flex-direction: column;
  align-items: center;
  border: dashed 2px red;
  border-radius: 5px;
  padding: 25px;
`;

const Details = styled.ul`
  /* display: flex; */
  /* gap: 5px; */
`;
const Item = styled.li`
  font-size: 20px;
`;

export default Confirmation;
