import { useState, useContext } from "react";

import Plane from "./Plane";
import styled from "styled-components";
import FlightSelect from "./FlightSelect";
import { AppContext } from "../AppContext";

const SeatSelect = () => {
  const { selectedFlight, selectedSeat } = useContext(AppContext);

  const handleSubmit = async (ev) => {
    // TODO if incorrect data entered, highlight which field it came from
    ev.preventDefault();
    try {
      const { email, lame, fname } = ev.currentTarget.elements;
      const reservationData = {
        flight: selectedFlight,
        seat: selectedSeat,
        givenName: fname.value,
        surname: lname.value,
        email: email.value,
      };
      const fnameValid = fname.value.length >= 1;
      const lnameValid = lname.value.length >= 1;
      const emailValid =
        email.value.indexOf("@") >= 1 &&
        email.value.indexOf(".") <= email.value.length - 3;
      if (fnameValid && lnameValid && emailValid) {
        const data = await fetch(`/api/get-flight?flight=${selectedFlight}`);
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <Wrapper>
      <FlightSelect />
      <h2>Select your seat and Provide your information!</h2>
      <div style={{ display: "flex" }}>
        <Plane />
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
          }}
        >
          <Inputs onSubmit={handleSubmit}>
            <input
              type="text"
              htmlFor="fname"
              name="fname"
              placeholder="First name"
            />
            <input
              type="text"
              htmlFor="lname"
              name="lname"
              placeholder="Last name"
            />
            <input
              type="text"
              htmlFor="email"
              name="email"
              placeholder="Email"
            />
            <input type="submit" value="Confirm" />
          </Inputs>
        </div>
      </div>
    </Wrapper>
  );
};

export default SeatSelect;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Inputs = styled.form`
  display: flex;
  flex-direction: column;
  border: 4px solid red;
  padding: 25px;
`;
