import { useContext } from "react";
import styled from "styled-components";

//
import { AppContext } from "../AppContext";
import Plane from "./Plane";
import CancelSuccessModal from "../CancelSuccessModal";
import DetailsInput from "./DetailsInput";

const SeatSelect = () => {
  const {
    showCancelSuccessModal,
    // setErrorMessage,
    // setLoading,
    // loading,
  } = useContext(AppContext);
  const inputType = "new";

  useEffect(() => {
    setSelectedSeat(currentReservation.seat);
    setSelectedFlight(currentReservation.flight);
  }, [third]);

  if (showCancelSuccessModal === true) {
    return <CancelSuccessModal />;
  }

  return (
    <Wrapper>
      <Borders>
        <Plane />
        <DetailsInput type={inputType} />
      </Borders>
    </Wrapper>
  );
};

export default SeatSelect;

const Wrapper = styled.div`
  display: flex;
  height: 100vh;
  /* margin: 0 29.5%; */
  align-items: center;
  justify-content: center;
  font-family: Kosugi;
`;
const Borders = styled.div`
  display: flex;
  width: var(--main-width);
  justify-content: space-around;
  height: 100%;
  padding: 50px;
  gap: 35px;
  border-left: 3px dashed var(--color-yellow);
  border-right: 3px dashed var(--color-yellow);
`;
