import styled from "styled-components";
import { useContext, useEffect } from "react";
import { NavLink, useHistory } from "react-router-dom";
import { AppContext } from "./AppContext";

const Header = () => {
  const {
    currentReservation,
    availableFlights,
    setAvailableFlights,
    setSelectedFlight,
    reservationId,
  } = useContext(AppContext);

  useEffect(() => {
    (async () => {
      try {
        const data = await fetch("/api/get-flights");
        const jsonifiedData = await data.json();
        setAvailableFlights(jsonifiedData.flights);
      } catch (err) {
        console.log(err);
      }
    })();
  }, [setAvailableFlights]);

  const history = useHistory();
  return (
    <HeaderTop>
      <Logo onClick={() => history.push("/")}>FlyinAir✈️</Logo>
    </HeaderTop>
  );
};

const HeaderTop = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: var(--color-dark-blue);
  height: 90px;
  padding: var(--padding-page) 24px;
  border-bottom: solid 2px white;
`;
const Logo = styled.div`
  transform: translateY(-2px);
  color: var(--color-lightest);
  font-family: "Fredoka One", cursive;
  text-shadow: -3px 0px var(--color-yellow);
  font-size: 60px;
  cursor: pointer;
`;
const Nav = styled.nav`
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;
const StyledNavLink = styled(NavLink)`
  background: var(--color-light-blue);
  border: 1px solid transparent;
  border-radius: 4px;
  color: var(--color-lightest);
  text-shadow: var(--color-red) 1px 1px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: var(--font-heading);
  font-size: 18px;
  height: 42px;
  margin: 0 0 0 8px;
  padding: 0 14px;
  width: 100%;
  text-decoration: none;
  transition: all ease 400ms;

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }

  &:hover {
    background: var(--color-yellow);
    color: var(--color-lightest);
    border-color: var(--color-lightest);
  }
`;

export default Header;
