import styled from "styled-components";
import { useContext, useEffect } from "react";
import { AppContext } from "./AppContext";
// import tombstone from "../assets/tombstone.png";

const Confirmation = () => {
  const { reservationId, currentReservation, setCurrentReservation } =
    useContext(AppContext);
  useEffect(() => {
    (async () => {
      try {
        const data = await fetch(
          `/api/get-reservation?reservationId=${reservationId}`
        );
        const { reservation } = await data.json();
        setCurrentReservation(reservation);
      } catch (err) {
        console.log(err);
      }
    })();
  }, [reservationId]);

  if (!currentReservation) return null;
  const { _id, flight, seat, givenName, surname, email } = currentReservation;

  return (
    <Wrapper>
      <BookingContainer>
        <h3>Your booking is confirmed!</h3>

        <li>Booking ID: {_id}</li>
        <li>Flight: {flight}</li>
        <li>Seat: {seat}</li>
        <li>
          Name: {givenName} {surname}
        </li>
        <li>Email: {email}</li>
      </BookingContainer>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  display: flex;
  height: 100%;
  justify-content: center;
  align-items: center;
  font-family: Kosugi;
`;

const BookingContainer = styled.div`
  border: dashed 2px red;
  border-radius: 5px;
  padding: 25px;
`;

export default Confirmation;
