import { useEffect, useState, useContext } from "react";
import { AppContext } from "../AppContext";
import styled from "styled-components";

const FlightSelect = () => {
  const { availableFlights, setAvailableFlights } = useContext(AppContext);
  useEffect(() => {
    (async () => {
      try {
        const data = await fetch("/api/get-flights");
        const jsonifiedData = await data.json();
        setAvailableFlights(jsonifiedData.flights);
      } catch (err) {
        console.log(err);
      }
    })();
  }, []);

  console.log(availableFlights);

  return (
    <Wrapper>
      <h1>Flight Number:</h1>
      <label for="flights">Select a flight:</label>
      <select name="flights" id="flights">
        <option disabled selected>
          Select a flight
        </option>
        {flights.map(({ _id }) => {
          return <option value={_id}>{_id}</option>;
        })}
      </select>
    </Wrapper>
  );
};

export default FlightSelect;

const Wrapper = styled.div`
  display: flex;
  width: 100%;
  height: 50px;
  padding: 0px 18px;
  background-color: #aa001e;
`;
